```typescript
// Standardized API error response
interface ApiError {
  code: string;           // Machine-readable: "AUTH_EXPIRED"
  message: string;        // Human-readable: "Your session has expired"
  details?: unknown;      // Additional context
  requestId?: string;     // For support/debugging
}

// Error factory
function createError(code: string, message: string, status: number): ApiError {
  return {
    code,
    message,
    requestId: crypto.randomUUID(),
  };
}

// Common errors
const Errors = {
  NOT_FOUND: (resource: string) => createError('NOT_FOUND', `${resource} not found`, 404),
  UNAUTHORIZED: () => createError('UNAUTHORIZED', 'Authentication required', 401),
  VALIDATION: (field: string) => createError('VALIDATION', `Invalid ${field}`, 400),
};
```
